import './FoodInfo.scss'
import {} from '../FoodSearch/FoodSlice'
import Grid from '@mui/material/Grid';
import {useSelector} from 'react-redux';
import Carousel from 'react-bootstrap/Carousel'

const FoodInfo=()=>{
 
  const {FoodInfo}=useSelector(store=>store.food)

  return(
    
 
      <div className='Food__Block'>
      <h2>{FoodInfo.label}</h2>
      

     {CarouselIngredients}
     </div>

    
  )
}
const CarouselIngredients=()=>{
  const {FoodInfo}=useSelector(store=>store.food)
  const element=FoodInfo.ingredients.map((item,index)=>{
    return(
      <CarouselItem
      key={index}
      {...item}
      />
    )
  })
  return(
    <Carousel>
      {element}
    </Carousel>
  )
}
const CarouselItem=({item})=>{
  <Carousel.Item>
          <img
              className="d-block w-100"
              src={item.image}
              alt={item.food}
          />
          <Carousel.Caption>
          <h3>{item.food}</h3>
          <p>{item.text}</p>
          </Carousel.Caption>
      </Carousel.Item>
}
export default FoodInfo;
