import './FoodInfo.scss'
import {} from '../FoodSearch/FoodSlice'
import Grid from '@mui/material/Grid';
import {useSelector} from 'react-redux';
import Carousel from 'react-bootstrap/Carousel'


const CarouselIngr=()=>{
  const {FoodInfo}=useSelector(store=>store.food)
  const element=FoodInfo.ingredients.map((item)=>{
    return(
      <Carousel.Item>
          <img
              className="d-block w-100"
              src={item.image}
              alt={item.food}
          />
          <Carousel.Caption>
          <h3>{item.food}</h3>
          <p>{item.text}</p>
          </Carousel.Caption>
      </Carousel.Item>
    )
  })
  return(
    <Carousel>
      {element}
    </Carousel>
  )
}

const FoodInfo=()=>{
 
  const {FoodInfo}=useSelector(store=>store.food)

  return(
    
    <Grid container spacing={2}>
      <div className='Food__Block'>
      <h2>{FoodInfo.label}</h2>
      </div>

     {CarouselIngr}
    </Grid>
    
  )
}

export default FoodInfo;
