import './FoodItems.scss';
import {useSelector} from 'react-redux';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Avatar from '@mui/material/Avatar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { red } from '@mui/material/colors';
// import FavoriteIcon from '@mui/icons-material/Favorite';
// import ShareIcon from '@mui/icons-material/Share';
// import MoreVertIcon from '@mui/icons-material/MoreVert';

const FoodList=()=>{

const {data,loading}=useSelector(store=>store.food)
const content =loading?<div>Загрузка</div>:  <FoodItems  data={data} />;
  return (
    <div >
      {content}    
    </div>
  )

}

const FoodItems=({data})=>{

  const elements = data.map((item,index) => {
    return(
      <FoodItem
        key={index}
        {...item}
      />
    )
      
  })
  return(
    
  <Box className='Food__Block' sx={{ flexGrow: 1 }}>
    <Grid container spacing={2}>
        {elements}
    </Grid>
  </Box>
  )
}



const FoodItem=(props)=>{
  console.log(props)
  return(
  
   <Grid item xs={4} >
     <Card sx={{ maxWidth: 345 }}>
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: red[500] }}  aria-label="recipe">
            R
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <p>More</p>
          </IconButton>
        }
        title={props.recipe.label}
        subheader={props.recipe.calories}
      />
      <CardMedia
        component="img"
        height="194"
        image={props.recipe.images.REGULAR.url}
        alt="Paella dish"
      />
      <CardContent>
        <Typography variant="body2" color="text.secondary">
          This impressive paella is a perfect party dish and a fun meal to cook
          together with your guests. Add 1 cup of frozen peas along with the mussels,
          if you like.
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
        <IconButton aria-label="add to favorites">
          <p>Favorite</p>
        </IconButton>
        <IconButton aria-label="share">
          <p>Share</p>
        </IconButton> 
      </CardActions>
      
    </Card>
   </Grid>
    
  )
}
export default FoodList;
