import './FoodInfo.scss'
import {} from '../FoodSearch/FoodSlice'
import Grid from '@mui/material/Grid';
import {useSelector} from 'react-redux';
import Carousel from 'react-bootstrap/Carousel'

const FoodInfo=()=>{
 
  const {FoodInfo}=useSelector(store=>store.food)
  const data=FoodInfo;
  return(
    
 
      <div className='Food__Block'>
      <h2>{FoodInfo.label}</h2>
      

      <CarouselIngredients data={data}/>
     </div>

    
  )
}
const CarouselIngredients=({data})=>{
  
  const element=data.ingredients.map((item,index)=>{
    return(
      <Item
      key={index}
      {...item}
      />
    )
  })
  return(
    <Carousel>
      {element}
    </Carousel>
  )
}
const Item=(props)=>{
  console.log('props=',props)
  
  return(
    <Carousel.Item>
          <img
              className="d-block w-100"
              src={props.image}
              alt={props.food}
          />
          <Carousel.Caption>
          <h3>{props.food}</h3>
          <p>{props.text}</p>
          </Carousel.Caption>
   </Carousel.Item>
  )
}

export default FoodInfo;
