import './FoodInfo.scss'
import Grid from '@mui/material/Grid';
import {useSelector} from 'react-redux';
import { Carousel } from 'react-carousel-minimal';

const FoodInfo=()=>{
 
  const {FoodInfo}=useSelector(store=>store.food)
  // console.log(FoodInfo.ingredients)
  const data=FoodInfo.ingredients;
  let arr=[];
  for(let i=0;i<data.length;i++){
    arr.push({
      image:`${data[i].image}`,
      caption:`${data[i].food}`})
  }
  console.log('arr=',arr)
  return(
    
 
      <div className='Food__Block'>
      <h2>{FoodInfo.label}</h2>
      <Grid justifyContent='center' container spacing={2}>
            <Grid item xs={4}>
            <Carousel
            data={data}
            time={2000}
            width="850px"
            height="500px"
            captionStyle={captionStyle}
            radius="10px"
            slideNumber={true}
            slideNumberStyle={slideNumberStyle}
            captionPosition="bottom"
            automatic={true}
            dots={true}
            pauseIconColor="white"
            pauseIconSize="40px"
            slideBackgroundColor="darkgrey"
            slideImageFit="cover"
            thumbnails={true}
            thumbnailWidth="100px"
            style={{
              textAlign: "center",
              maxWidth: "850px",
              maxHeight: "500px",
              margin: "40px auto",
            }}
          />
              <div>nadfua</div>
            </Grid>
      </Grid>
      

    
     </div>   
  )
}
const FoodItems=({data})=>{
  const captionStyle = {
    fontSize: '2em',
    fontWeight: 'bold',
  }
  const slideNumberStyle = {
    fontSize: '20px',
    fontWeight: 'bold',
  }
   const elements=data.map((item)=>{
     return(
      <div className='Food__Block'>
          <img className='Food__Img' src={item.image} />
          <p className="legend">Legend 1</p>
      </div>
     )
   })
   return(
     <Carousel>
       {elements}
     </Carousel>
   )

}
export default FoodInfo;
