import { useHttp } from "../hooks/useHttp";

const useIpServices=()=>{
  const _apiBase='http://ipwhois.app/json/'
 

  const getUserIp=async()=>{
    const res= await request(`${_apiBase}`)
    return res
  }
  

  return {getUserIp}
  
}
export default useIpServices;
