import axios from 'axios';

export const API_URL='http://localehost:5000/api'

const $api = axios.create({
  withCredentials: true,
  baseURL:API_URL
})

$api.interceptors.request.use((config) => {//после каждого запроса записывем token в localeStorage
  config.headers.Autorization = `Bearer ${localStorage.getItem('token')}`
  return config;
})

export default $api;
