import {createSlice,createAsyncThunk,current} from "@reduxjs/toolkit"
import useMarvelServices from "../../services/MarvelServices"

const initialState = {
  data:[],
  newItemLoading:false,
  offset:211,
  charEnded:false,
  selectedId:-1,
  firstUpd:true,
}

export const getCharacters=createAsyncThunk(
  'char/getCharacters',
  async(state)=>{
    const { getAllCharacters }=useMarvelServices();
    const response=getAllCharacters(state)
    return response;
  }
)
export const addUserCity=createAsyncThunk(
  'weather/addUserCity',
   async(state)=>{
    const {getWeatherByCity}=useWeatherServices();
    const response= await getWeatherByCity(state)
    return response
  }
)



const CharSlice=createSlice({
  name:'char',
  initialState,
  reducers: {
      
  },
  extraReducers: (builder) => {
    builder
      .addCase(getCharacters.pending,(state,action)=>{
        console.log('Loading Character')
      })
      .addCase(getCharacters.fulfilled,(state,action)=>{
        console.log(action)
        console.log('yes')
      })
      .addCase(getCharacters.rejected,(state,action)=>{
        console.log(action?.error?.message)
      })
       
}
})

const {actions, reducer} = CharSlice;

export default reducer;

export const {
  
} = actions;
