import './FoodInfo.scss'
import Grid from '@mui/material/Grid';
import {useSelector} from 'react-redux';
import Carousel from 'react-bootstrap/Carousel'

const FoodInfo=()=>{
 
  const {FoodInfo}=useSelector(store=>store.food)
  console.log(FoodInfo)
  return(
    
 
      <div className='Food__Block'>
      <h2>{FoodInfo.label}</h2>
      

      {/* <CarouselIngredients data={data}/> */}
      <Carousel>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src={FoodInfo.ingredients[0].image}
      alt="First slide"
    />
    <Carousel.Caption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src={FoodInfo.ingredients[1].image}
      alt="Second slide"
    />

    <Carousel.Caption>
      <h3>Second slide label</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-10"
      src={FoodInfo.ingredients[2].image}
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Third slide label</h3>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>
     </div>

    
  )
}
const CarouselIngredients=({data})=>{
  
  const element=data.ingredients.map((item,index)=>{
    return(
      <Item
      key={index}
      {...item}
      />
    )
  })
  return(
    <Carousel>
      {element}
    </Carousel>
  )
}
const Item=(props)=>{
  console.log('props=',props)
  
  return(
    <Carousel.Item>
          <img
              className="d-block w-100"
              src={props.image}
              alt={props.food}
          />
          <Carousel.Caption>
          <h3>{props.food}</h3>
          <p>{props.text}</p>
          </Carousel.Caption>
   </Carousel.Item>
  )
}

export default FoodInfo;
