import './FoodSearch.scss'
import {useState,useEffect} from 'react'
import useEdamamServices from '../../services/Edamam'
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';

const FoodSearch=()=>{
  const [variables,setVariables]=useState([])
  const {getAutoCompete}=useEdamamServices()
  const [food,setFood]=useState('')

  useEffect(()=>{
    getAutoCompete('pizza').then(Loading)
  },[])

  useEffect(async()=>{
    if(weatherCity.length>3) {
      const response= await getAutoComplete(weatherCity)
      setVariables(response)
    }
  },[food])

  const onUpdateFood=(e)=>{
    setFood(e.target.value)
  }

  const Loading=(res)=>{
    console.log(res)
  }
  return(
    <>
     <Stack spacing={2} sx={{ width: 300 }}>
      <Autocomplete
        id="free-solo-demo"
        freeSolo
        options={variables.map((option) => option)}
        renderInput={(params) => <TextField {...params} 
                                            value={food}
                                            onChange={onUpdateFood} 
                                            label="Add..." />}
      />
     
    </Stack>
    </>
  )
}
export default FoodSearch;
